package com.example.darinternshiptask3

import android.util.Log
import java.util.*

class StudentsStorage {
    private var idStudent = 1000
    private val tempTrash = Stack<Student>()
    private val studentsSet = LinkedList<Student>()
    private val dummyBase64: String = ""

    companion object {
        private var instance: StudentsStorage? = null
        fun getInstance(): StudentsStorage? {
            if (instance == null) {
                synchronized(this) {
                    instance = StudentsStorage()
                }
            }
            return instance
        }
    }

    fun getId() = idStudent

    fun hasStudent(student: Student) = studentsSet.contains(student)
    
    fun addStudent(student: Student) {
        studentsSet.add(student)
        Log.i("restoreFromTrash", student.name.toString())
    }

    fun updateId() {
        idStudent++
    }

    fun getStudents() = studentsSet.toList()

    fun createDummyStudents() {
        for (i in 1..5) {
            studentsSet.add(Student(name = "Meiirbek$i", id = i + 100, image = dummyBase64))
        }
    }

    fun deleteStudent(student: Student) {
        studentsSet.remove(student)

        if (tempTrash.size < 5) {
            tempTrash.push(student)
        } else {
            tempTrash.removeFirstOrNull()
            tempTrash.push(student)
        }
    }

    fun restoreLastDeleted() {
        if (tempTrash.isEmpty()) return
        addStudent(tempTrash.pop())
    }

}